import React from "react";
import { useParams, Route, Link, useRouteMatch } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedJoke from "../components/jokes/HighlightedJoke";

const DUMMY_JOKES = [
  {
    id: "j1",
    topic: "Programming",
    text: "Knock, knock. Who’s There? Very long pause… “Java.”",
  },
  {
    id: "j2",
    topic: "Programming",
    text: "Programming is 10% writing code and 90% understanding why it’s not working",
  },
  { id: "j3", topic: "General", text: "RIP, boiling water. You will be mist." },
];

const JokeDetails = () => {
  const params = useParams();
  const routeMatch = useRouteMatch();
  console.log(routeMatch);
  const joke = DUMMY_JOKES.find((joke) => joke.id === params.jokeId);

  if (!joke) {
    return <h1 className="centered">No Content</h1>;
  }

  return (
    <React.Fragment>
      <HighlightedJoke topic={joke.topic} text={joke.text} />
      <Route path={`${routeMatch.path}`} exact>
        <div className="centered">
          <Link
            className="btn--empty"
            to={`/joke-list/${params.jokeId}/comments`}
          >
            Show Comments
          </Link>
        </div>
      </Route>

      <Route path={`${routeMatch.path}/comments`}>
        <Comments />
      </Route>
    </React.Fragment>
  );
};

export default JokeDetails;
