import React from "react";
import { useParams, Route } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedJoke from "../components/jokes/HighlightedJoke";

const DUMMY_JOKES = [
  {
    id: "j1",
    topic: "Programming",
    text: "Knock, knock. Who’s There? Very long pause… “Java.”",
  },
  {
    id: "j2",
    topic: "Programming",
    text: "Programming is 10% writing code and 90% understanding why it’s not working",
  },
  { id: "j3", topic: "General", text: "RIP, boiling water. You will be mist." },
];

const JokeDetails = () => {
  const params = useParams();
  const joke = DUMMY_JOKES.find((joke) => joke.id === params.jokeId);
  return (
    <React.Fragment>
      <HighlightedJoke topic={joke.topic} text={joke.text} />
      <h1>Jokes Details Page {params.jokeId}</h1>
      <Route path="/jokes/:jokeId/comments">
        <Comments />
      </Route>
    </React.Fragment>
  );
};

export default JokeDetails;
